using System.Text.Json;
using FluentAssertions;
using Mvp.Selections.Domain;
using Mvp.Selections.Domain.Roles;
using Xunit;

// ReSharper disable StringLiteralTypo
namespace Mvp.Selections.Client.Tests.Serialization
{
    public class RoleConverterTests
    {
        [Fact]
        public void CanSerializeSystemRole()
        {
            // Arrange
            Role role = new SystemRole(Guid.NewGuid())
            {
                Name = "Test",
                Users =
                [
                    new User(Guid.NewGuid())
                    {
                        Name = "Test"
                    }
                ],
                CreatedOn = DateTime.Today.AddDays(-1),
                CreatedBy = "System",
                ModifiedOn = DateTime.Today,
                ModifiedBy = "Test",
                Rights = Right.Apply | Right.Review
            };
            using MemoryStream ms = new();

            // Act
            JsonSerializer.Serialize(ms, role, SerializationSettings.GetOptions());

            // Assert
            ms.Flush();
            ms.Position = 0;
            using TextReader reader = new StreamReader(ms);
            string json = reader.ReadToEnd();

            JsonDocument.Parse(json);
            json.Should().MatchRegex(".*\"\\$type\":\"Mvp.Selections.Domain.Roles.SystemRole, Mvp.Selections.Domain\".*");
            json.Should().MatchRegex(".*\"rights\":\"Apply, Review\".*");
        }

        [Fact]
        public void CanSerializeSelectionRole()
        {
            // Arrange
            Role role = new SelectionRole(Guid.NewGuid())
            {
                Name = "Test",
                Users =
                [
                    new User(Guid.NewGuid())
                    {
                        Name = "Test"
                    }
                ],
                CreatedOn = DateTime.Today.AddDays(-1),
                CreatedBy = "System",
                ModifiedOn = DateTime.Today,
                ModifiedBy = "Test",
                Country = new Country(1)
                {
                    Name = "Country A",
                    CreatedOn = DateTime.Today.AddDays(-1),
                    CreatedBy = "System",
                    Region = new Region(1)
                    {
                        Name = "Region A",
                        CreatedOn = DateTime.Today.AddDays(-1),
                        CreatedBy = "System"
                    }
                }
            };
            using MemoryStream ms = new();

            // Act
            JsonSerializer.Serialize(ms, role, SerializationSettings.GetOptions());

            // Assert
            ms.Flush();
            ms.Position = 0;
            using TextReader reader = new StreamReader(ms);
            string json = reader.ReadToEnd();

            JsonDocument.Parse(json);
            json.Should().MatchRegex(".*\"\\$type\":\"Mvp.Selections.Domain.Roles.SelectionRole, Mvp.Selections.Domain\".*");
            json.Should().MatchRegex(".*\"country\":\\{\"name\":\"Country A\".*");
        }

        [Fact]
        public void CanDeserializeSystemRole()
        {
            // Arrange
            const string json =
                "{\"$type\":\"Mvp.Selections.Domain.Roles.SystemRole, Mvp.Selections.Domain\",\"rights\":\"Apply, Review\",\"name\":\"Test\",\"id\":\"579b081b-3c39-498f-b770-9ed447a44fd4\",\"createdOn\":\"2022-09-11T00:00:00\",\"createdBy\":\"System\",\"modifiedOn\":\"2022-09-12T00:00:00\",\"modifiedBy\":\"Test\"}";

            // Act
            Role? role = JsonSerializer.Deserialize<Role>(json, SerializationSettings.GetOptions());

            // Assert
            role.Should().NotBeNull();
            role.Should().BeOfType<SystemRole>();
            ((SystemRole)role!).Rights.Should().Be(Right.Apply | Right.Review);
        }

        [Fact]
        public void CanDeserializeSelectionRole()
        {
            // Arrange
            const string json =
                "{\"$type\":\"Mvp.Selections.Domain.Roles.SelectionRole, Mvp.Selections.Domain\",\"country\":{\"name\":\"Country A\",\"region\":{\"name\":\"Region A\",\"countries\":[],\"id\":1,\"createdOn\":\"2022-09-11T00:00:00+02:00\",\"createdBy\":\"System\"},\"users\":[],\"id\":1,\"createdOn\":\"2022-09-11T00:00:00+02:00\",\"createdBy\":\"System\"},\"name\":\"Test\",\"id\":\"8fd1fe8b-3724-4759-b4c8-1a3a45f3a470\",\"createdOn\":\"2022-09-11T00:00:00\",\"createdBy\":\"System\",\"modifiedOn\":\"2022-09-12T00:00:00\",\"modifiedBy\":\"Test\"}";

            // Act
            Role? role = JsonSerializer.Deserialize<Role>(json, SerializationSettings.GetOptions());

            // Assert
            role.Should().NotBeNull();
            role.Should().BeOfType<SelectionRole>();
            ((SelectionRole)role!).Country!.Name.Should().Be("Country A");
            ((SelectionRole)role).Country!.Region!.Name.Should().Be("Region A");
        }

